import React, { Component } from 'react';
import axios from 'axios';

class Product_search extends Component {
    state = {
        listsUsers: [],
    }
    async componentDidMount() {
        let res = await axios.get('https://621fa031ce99a7de19438436.mockapi.io/users');
        this.setState ({
            listsUsers: res && res.data && res.data.data ? res.data.data : []
        })

    }
    render() {
        let { listsUsers } = this.state;
        return (
            <div className="col l-9 product">
            <div className="row product__heading">
              <h2>new products
              </h2>
            </div>
            <div className="row">
            {listsUsers && listsUsers.length > 0 && listsUsers.map((item, index) =>{
                    return (
                        <div className="col l-4" key={item.id}>
                        <div className="product-new">
                          <div className="product__header">
                            <img src="./anh1.jpg" alt="" className="product__header-img" />
                            <div className="product__header-evaluate" />
                          </div>
                          <div className="product__title">
                            <h3 className="product__title-heading">{item.name}</h3>
                            <h3 className="product__title-heading">{item.des}</h3>
                            <h2 className="product__title-price">$94</h2>
                            <button className="product__title-btn">ADD To Cart</button>
                          </div>
                        </div>
                      </div>
                    )
                })}
            </div>
          </div>
        )
    }
}

export default Product_search;
