import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import  ApiCaller from './../Utils/ApiCaller'



class Detaillproduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
        user:{}
    }
  }
  
     componentDidMount() {
      let id = this.props.match.params.id;
        ApiCaller(`Product/${id}`,'Get',null)
          .then((res) => {
            this.setState({
              user:res.data
            });
          })
    }
    back = () => {
        
        this.props.history.push(`/Product`)
    }
    render() {
        let {user} = this.state;
        let isemty = Object.keys(user).length === 0;
        return (
             <div className="col l-9 product">
            <div className="row product__heading">
              <h2>new products
              </h2>
            </div>
              <div className="row">
                  
              <button className="product__title-btn" type="button" onClick={() => this.back()}>ADD To Cart</button>
               {isemty === false && 
                <>
                       <div className="col l-4"  key={user.id}>
                       <div className="product-new">
                         <div className="product__header">
                           <img src={user.img} alt="" className="product__header-img" />
                           <div className="product__header-evaluate" />
                         </div>
                         <div className="product__title">
                           <h3 className="product__title-heading">trung {user.name}</h3>
                           <h3 className="product__title-heading"> gia: {user.des}</h3>
                           <h2 className="product__title-price">$94</h2>
                           <button className="product__title-btn">ADD To Cart</button>
                         </div>
                       </div>
                     </div>
                     </>
                 }
                </div>
          </div>
        );
    }
}

export default withRouter(Detaillproduct);
