import  { useState ,useEffect } from 'react';
import axios from 'axios';

function Test() {
//  sử dụng two way biding
// khai báo useState
const [data, setData] = useState({ hits: [] });
  
  useEffect(async () => {
    const result = await axios(
      'https://621fa031ce99a7de19438436.mockapi.io/users',
    );
    setData(result.data);
  });

  return (
    <ul>
      {data.hits.map(item => (
        <li key={item.objectID}>
          <a href={item.url}>{item.title}</a>
        </li>
      ))}
    </ul>
  );
      }
export default Test;
