import React, { useState, useEffect }    from 'react';
import { useHistory } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    useEffect(() =>{
        if(localStorage.getItem('use-info'))(
           this.post.history.push("/Product")
        )
    },[])
    async function handleLogin(){
       console.log(username,password);
       let item = {username,password};
       let result = await fetch("https://621fa031ce99a7de19438436.mockapi.io/user",{
           method: 'POST',
           headers: {
               "Content-Type": "application/json",
               "Accept": "application/json"
           },
           body: JSON.stringify(item)
       });
       result = await result.json();
       localStorage.setItem("use-info",JSON.stringify(result))
       History.push('/Product')
   }

    return (
        <div className="col l-9 product">
        <div className="row product__heading">
        </div>
          <div className="row">
        <div className="login-box">
        <h2>Login</h2>
          <div className="user-box">
            <input type="text" 
            onChange={e => setUsername(e.target.value)}
            required />
            <label>Username</label>
          </div>
          <div className="user-box">
            <input type="password" 
             onChange={e => setPassword(e.target.value)}
            required />
            <label>Password</label>
          </div>
          <input 
            type="button"
            onClick={handleLogin}
            value="Login"
          />
      </div>
      </div>
      </div>
      
    );
}

export default Login;
