import  { useState } from 'react';
import axios from 'axios';

function Test() {
//  sử dụng two way biding
// khai báo useState
const [name, setName] =  useState();
const [mail, setmail] = useState();


const handsubmits = () => {
    let login = {name, mail};
    axios.get("https://621fa031ce99a7de19438436.mockapi.io/login")
        .then((res) => {
          this.setState({ listsUsers:res.data});

        }).catch((err)=>{
            console.log(err)
        })
    }


    return (
        <form>
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">Email address</label>
                <input values={name} onChange={ e => setName(e.target.value)}
                type="text" className="form-control" id="exampleInputEmail1"  placeholder="Enter email" />
            </div>
            <div className="form-group">
                <label htmlFor="exampleInputPassword1">Password</label>
                <input values={mail} onChange={ e => setmail(e.target.value)}
                 type="email" className="form-control" id="exampleInputPassword1" placeholder="Password" />
            </div>
            <button type="submit" className="btn btn-primary" onClick={handsubmits}>Submit</button>
        </form>

    );
}

export default Test;
