import  { useState ,useEffect } from 'react';
import axios from 'axios';

function Test() {
//  sử dụng two way biding
// khai báo useState
const [name, setName] =  useState('');
const [mail, setmail] = useState('');
const [data, setData] = useState({ hits: [] });
useEffect(async () => {
    axios.get("https://621fa031ce99a7de19438436.mockapi.io/users")
    .then((res) => {
      this.setState({ listsUsers:res.data});

    }).catch((err)=>{
        console.log(err)
    })
  });
  
  return (
    <ul>
      {data.hits.map(item => (
        <li key={item.id}>
          <h3> {item.name}</h3>
          <h3>{item.des}</h3>
        </li>
      ))}
    </ul>
  );
}

export default Test;
