import React, { Component } from 'react';
import axios from 'axios';
import paginate from 'paginate-array';

class Product_search extends Component {
    state = {
        listsUsers: [],
        size: 5,
        page: 1,
        currPage: null,
    }
    
  
      
  
      componentDidMount() {
        axios.get("https://621fa031ce99a7de19438436.mockapi.io/users")
        .then((res) => {
          this.setState({ listsUsers:res.data});

        })
        .then(listsUsers => {
          const { page, size } = this.state;
  
          const currPage = paginate(listsUsers, page, size);
  
          this.setState({
            ...this.state,
            listsUsers,
            currPage
          });
        }).catch((err)=>{
            console.log(err)
        })
      }
      previousPage() {
        const { currPage, page, size, listsUsers } = this.state;
    
        if (page > 1) {
          const newPage = page - 1;
          const newCurrPage = paginate(listsUsers, newPage, size);
    
          this.setState({
            ...this.state,
            page: newPage,
            currPage: newCurrPage
          });
        }
      }
      nextPage() {
        const { currPage, page, size, listsUsers } = this.state;
    
        if (page < currPage.totalPages) {
          const newPage = page + 1;
          const newCurrPage = paginate(listsUsers, newPage, size);
          this.setState({ ...this.state, page: newPage, currPage: newCurrPage });
        }
      }
    render() {
      const { listsUsers } = this.state;
        return (
            <div className="col l-9 product">
            <div className="row product__heading">
              <h2>new products
              </h2>
            </div>
              <div className="row">
               {listsUsers?.map((item, index) =>{
                    return (
                       <div className="col l-4" key={item.id}>
                       <div className="product-new">
                         <div className="product__header">
                           <img src="./anh1.jpg" alt="" className="product__header-img" />
                           <div className="product__header-evaluate" />
                         </div>
                         <div className="product__title">
                           <h3 className="product__title-heading">trung {item.name}</h3>
                           <h3 className="product__title-heading"> gia: {item.des}</h3>
                           <h2 className="product__title-price">$94</h2>
                           <button className="product__title-btn">ADD To Cart</button>
                         </div>
                       </div>
                     </div>
                    )
                  })
                }
                 <button onClick={this.previousPage}>PreviousPage</button>
                   <button onClick={this.nextPage}>Next Page</button>
            </div>
          </div>
        )
    }
}

export default Product_search;
