import  { useState ,useEffect } from 'react';
import axios from 'axios';



function Test() {
//  sử dụng two way biding
// khai báo useState
const [data, setData] = useState([]);

      useEffect( () => {
       axios.get('https://621fa031ce99a7de19438436.mockapi.io/product', )
       .then( (res) => {
        setData(res.data)
       }).catch ((e) => {
         console.error(e);
       })
        }
        )

        // useEffect( () => {
        // const  viewDetaill =(user) => {
        //     this.props.history.push(`/Detaillproduct/${viewDetaill.id}`)
        //   }
        // })

  return (
    <div className="col l-9 product">
        <div className="row product__heading">
        <button className="home-filter__btn btn">Phổ Biến</button>
          <h2>new products
          </h2>
        </div>
          <div className="row">
                {data.map(item => (
                   <div className="col l-4" key={item.id} onClick={ () =>this.viewDetaill(item)}>
                   <div className="product-new">
                     <div className="product__header">
                       <img src={item.img} alt="" className="product__header-img" />
                       <div className="product__header-evaluate" />
                     </div>
                     <div className="product__title">
                       <h3 className="product__title-heading">trung {item.name}</h3>
                       <h2 className="product__title-price">$94</h2>
                       <button className="product__title-btn">ADD To Cart</button>
                     </div>
                   </div>
                 </div>
                ))}
                </div>
                </div>
            );
      }
export default Test;
