import React, { useState }    from 'react';
import axios from 'axios';

function Login(props) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const getUser = () => {
        const usertr = sessionStorage.getItem("user");
        if(usertr)return JSON.parse(usertr);
        else return null;
    }
    const getToken = () => {
            return sessionStorage.getItem("token") || null;
    }
    const setUserSession = (token,user) => {
        sessionStorage.getItem("token", token);
        sessionStorage.getItem("user" , JSON.stringify(user));
    }
    const removeUserSession = () => {
        sessionStorage.removeItem("token" );
        sessionStorage.removeItem("token" );
    }


    const handleLogin = () => {
        setError(null);
        setLoading(true);
       axios.post("https://621fa031ce99a7de19438436.mockapi.io/user",{
           username :username,
           password :password
       }) .then((response) => {
           setLoading(false);
           setUserSession()
       }).catch((error) => {
           console.log('error', error);
       });
    }





    return (
        <div className="col l-9 product">
        <div className="row product__heading">
        </div>
          <div className="row">
        <div className="login-box">
        <h2>Login</h2>
        <form>
          <div className="user-box">
            <input type="text" 
            value={username}
            onChange={e => setUsername(e.target.value)}
            required />
            <label>Username</label>
          </div>
          <div className="user-box">
            <input type="password" 
             value={password}
             onChange={e => setPassword(e.target.value)}
            required />
            <label>Password</label>
          </div>
          <input 
            type="button"
            value={loading ? "Loading..." : "Login"}
            disabled={loading}
            onClick={handleLogin}
          />
        </form>
      </div>
      </div>
      </div>
      
    );
}

export default Login;
